import React, { useState } from 'react';
import axios from 'axios';

const TodoList = ({ todoData, setTodoData }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [currentTodo, setCurrentTodo] = useState(null);
    const [editedTitle, setEditedTitle] = useState('');

    const deleteTodo = (id) => {
        const options = {
            method: "DELETE",
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: "application/json",
            }
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => prevData.filter(todo => todo._id !== id))
            })
            .catch((err) => {
                console.log(err)
            })
    };

    const enterEditMode = (todo) => {
        setIsEditing(true);
        setCurrentTodo(todo);
        setEditedTitle(todo.title);
    };

    const editTodo = (id) => {
        const options = {
            method: "PUT",
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: "application/json",
                "Content-Type": "application/json"
            },
            data: { title: editedTitle }
        };
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data);
                setTodoData(prevData => prevData.map(todo => 
                    todo._id === id ? { ...todo, title: editedTitle } : todo
                ));
                setIsEditing(false);
                setCurrentTodo(null);
                setEditedTitle('');
            })
            .catch((err) => {
                console.log(err);
            });
    };

    return (
        <div>
            {todoData.map(todo => (
                <div key={todo._id}>
                    {isEditing && currentTodo && currentTodo._id === todo._id ? (
                        <div>
                            <input 
                                type="text" 
                                value={editedTitle} 
                                onChange={(e) => setEditedTitle(e.target.value)} 
                            />
                            <button onClick={() => editTodo(todo._id)}>Save</button>
                            <button onClick={() => setIsEditing(false)}>Cancel</button>
                        </div>
                    ) : (
                        <div>
                            <span>{todo.title}</span>
                            <button onClick={() => deleteTodo(todo._id)}>Delete</button>
                            <button onClick={() => enterEditMode(todo)}>Edit</button>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default TodoList;
